import restArguments from 'underscore/modules/restArguments.js';
import flatten from 'underscore/modules/_flatten.js';
import filter from 'underscore/modules/filter.js';
import contains from 'underscore/modules/contains.js';

// Take the difference between one array and a number of other arrays.
// Only the elements present in just the first array will remain.
export default restArguments(function(array, rest) {
  rest = flatten(rest, true, true);
  return filter(array, function(value){
    return !contains(rest, value);
  });
});
