import restArguments from 'underscore/modules/restArguments.js';
import isFunction from 'underscore/modules/isFunction.js';
import negate from 'underscore/modules/negate.js';
import map from 'underscore/modules/map.js';
import flatten from 'underscore/modules/_flatten.js';
import contains from 'underscore/modules/contains.js';
import pick from 'underscore/modules/pick.js';

// Return a copy of the object without the disallowed properties.
export default restArguments(function(obj, keys) {
  var iteratee = keys[0], context;
  if (isFunction(iteratee)) {
    iteratee = negate(iteratee);
    if (keys.length > 1) context = keys[1];
  } else {
    keys = map(flatten(keys, false, false), String);
    iteratee = function(value, key) {
      return !contains(keys, key);
    };
  }
  return pick(obj, iteratee, context);
});
